EquipRefineStarInfo = {}
function InitRefineStarInfo()
   for k,v in pairs(EquipRefineStarConfig) do
       local RefineStarEquiptID = v.EquiptID
       local RefineStarMaxStar = v.MaxStar
       local RefineStarmaterialid = v.needmaterialid

       if GetTableLenth(RefineStarEquiptID) ~= GetTableLenth(RefineStarMaxStar) then
            rfalse("InitRefineStarInfo "..k.." tablenum Faile")
            break
       end

        if GetTableLenth(RefineStarEquiptID) ~= GetTableLenth(RefineStarmaterialid) then
            rfalse("InitRefineStarInfo "..k.." tablenum Faile")
            break
       end

       local starnum = GetTableLenth(RefineStarEquiptID) 
       for i=1,starnum do
            local itemid = RefineStarEquiptID[i]
           EquipRefineStarInfo[itemid] = {}
           EquipRefineStarInfo[itemid].AddPropertyID = v.AddPropertyID
           EquipRefineStarInfo[itemid].MaxStar = RefineStarMaxStar[i]
           EquipRefineStarInfo[itemid].needmaterialid = RefineStarmaterialid[i]
           EquipRefineStarInfo[itemid].needmaterialnum = {}
           EquipRefineStarInfo[itemid].RefineStarValue = {}
           EquipRefineStarInfo[itemid].moneyconst = {}
           EquipRefineStarInfo[itemid].successrate = {}
           for j=1,RefineStarMaxStar[i] do
               table.insert(EquipRefineStarInfo[itemid].needmaterialnum,v.needmaterialnum[j])
               table.insert(EquipRefineStarInfo[itemid].RefineStarValue,v.RefineStarValue[j])
               table.insert(EquipRefineStarInfo[itemid].moneyconst,v.moneyconst[j])
               table.insert(EquipRefineStarInfo[itemid].successrate,v.successrate[j])
           end
       end
   end
